SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_mat_prescr_horario (hr_inicial_p bigint, hr_final_p bigint, nr_prescricao_p bigint) RETURNS varchar AS $body$
DECLARE



ds_retorno_w		varchar(4000)	:= 'N';
cd_material_w		bigint;
ds_material_w		varchar(255);
ds_horarios_w		varchar(2000);
hr_atual_w		bigint;

c01 CURSOR FOR
	SELECT	a.cd_material,
		substr(b.ds_material,1,255),
		a.ds_horarios
	from	material b,
		prescr_material a
	where	a.cd_material	= b.cd_material
	and	a.nr_prescricao	= nr_prescricao_p;


BEGIN

ds_retorno_w	:= 'N';

open	c01;
loop
fetch	c01 into
	cd_material_w,
	ds_material_w,
	ds_horarios_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	hr_atual_w	:= hr_inicial_p;

	while(hr_atual_w	<= hr_final_p) loop
		begin

		if (position(hr_atual_w in ' ' || ds_horarios_w || ' ') > 0) then
			ds_retorno_w	:= 'S';
		end if;


		hr_atual_w	:= hr_atual_w + 1;

		end;
	end loop;

	end;
end loop;
close	c01;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
