CREATE OR REPLACE FUNCTION obter_se_mat_prim_vez_prescr ( nr_atendimento_p bigint, cd_material_p bigint, ie_liberacao_p text, ie_agrupador_p bigint) RETURNS varchar AS $body$
DECLARE


ie_prim_vez_prescr_w	varchar(1) := 'S';
ie_liberacao_w			varchar(1) := 'E';
ie_agrupador_w			smallint	:= 1;


BEGIN

if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') and (cd_material_p IS NOT NULL AND cd_material_p::text <> '') then

	ie_liberacao_w	:= coalesce(ie_liberacao_p, ie_liberacao_w);
	ie_agrupador_w	:= coalesce(ie_agrupador_p, ie_agrupador_w);

	select	coalesce(max('N'), 'S')
	into STRICT	ie_prim_vez_prescr_w
	from	prescr_material b,
			prescr_medica a
	where	1 = 1
	and		b.ie_agrupador = ie_agrupador_w
	and		b.cd_material = cd_material_p
	and		coalesce(b.dt_suspensao::text, '') = ''
	and		a.nr_prescricao = b.nr_prescricao
	and		(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '' AND ie_liberacao_p = 'E')
	and		coalesce(a.dt_suspensao::text, '') = ''
	and		a.nr_atendimento = nr_atendimento_p;

end if;

return	ie_prim_vez_prescr_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
