CREATE OR REPLACE FUNCTION obter_se_mat_rec_alerta ( cd_material_p bigint, nr_seq_regra_p bigint) RETURNS varchar AS $body$
DECLARE


ie_recebe_w				varchar(1) := 'S';
qt_existe_regra_w		bigint;
cd_grupo_material_w	bigint;
cd_subgrupo_w			bigint;
cd_classe_material_w	bigint;


BEGIN


if (coalesce(nr_seq_regra_p,0) > 0) then
	select	count(*)
	into STRICT		qt_existe_regra_w
	from		regra_envio_sms_material
	where		nr_seq_regra = nr_seq_regra_p;

	select	coalesce(max(cd_grupo_material),0),
				coalesce(max(cd_subgrupo_material),0),
				coalesce(max(cd_classe_material),0)
	into STRICT		cd_grupo_material_w,
				cd_subgrupo_w,
				cd_classe_material_w
	from		estrutura_material_v
	where 	cd_material	= cd_material_p;

	if (qt_existe_regra_w = 0) then
		ie_recebe_w := 	'S';
	else
		select	coalesce(max('S'),'N')
		into STRICT		ie_recebe_w
		from		regra_envio_sms_material
		where		nr_seq_regra 	= nr_seq_regra_p
		and		((coalesce(cd_material,coalesce(cd_material_p,0))	= coalesce(cd_material_p,0)) and (coalesce(cd_grupo_material,cd_grupo_material_w) = cd_grupo_material_w) and (coalesce(cd_subgrupo_material,cd_subgrupo_w) = cd_subgrupo_w) and (coalesce(cd_classe_material,cd_classe_material_w) = cd_classe_material_w));

	end if;
end if;

return	ie_recebe_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
