CREATE OR REPLACE FUNCTION obter_se_mat_regra_subs_solic ( cd_material_p bigint, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


qt_registro_w		bigint;
ie_existe_w		varchar(1) := 'N';


BEGIN

select	count(*)
into STRICT	qt_registro_w
from	sup_regra_subs_material a,
	sup_lista_subs_material b
where	a.nr_sequencia		= b.nr_seq_regra
and	a.cd_estabelecimento	= cd_estabelecimento_p
and	a.cd_material		= cd_material_p
and	a.ie_solicitacao		= 'S';

if (qt_registro_w > 0) then
	ie_existe_w := 'S';
end if;

return	ie_existe_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
