CREATE OR REPLACE FUNCTION obter_se_mat_repasse_valido (nr_interno_conta_p bigint, nr_seq_criterio_p bigint) RETURNS varchar AS $body$
DECLARE

cd_material_w		bigint;
qt_mat_w		integer;
ie_criterio_valido_w	varchar(1);
qt_regra_w		bigint	:= 0;

/*
'S' - Critério Válido
'N' - Critério inválido
*/
c01 CURSOR FOR
	SELECT 	cd_material
	from	proc_criterio_rep_mat
	where	nr_seq_criterio = nr_seq_criterio_p;

BEGIN

ie_criterio_valido_w	:= 'S';

select	count(1)
into STRICT	qt_regra_w
from	proc_criterio_rep_mat
where	nr_seq_criterio = nr_seq_criterio_p  LIMIT 1;

if (qt_regra_w <> 0) then
	OPEN C01;
	LOOP
	FETCH C01 into
		cd_material_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin

		if (ie_criterio_valido_w = 'S') then

			select 	count(1)
			into STRICT	qt_mat_w
			from 	material_atend_paciente
			where	cd_material		= cd_material_w
			and	nr_interno_conta 	= nr_interno_conta_p  LIMIT 1;

			if (qt_mat_w = 0) then

				ie_criterio_valido_w := 'N';

			end if;
		end if;

		end;
	END LOOP;
	CLOSE C01;
end if;

return ie_criterio_valido_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
