CREATE OR REPLACE FUNCTION obter_se_mat_risco_lote ( cd_setor_atendimento_p bigint, nr_lote_agrupamento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_material_risco_w	varchar(1) := 'N';	
qt_existe_w			bigint;

lotesAgrupados CURSOR FOR
		SELECT	b.cd_material
		from 	ap_lote a,
				ap_lote_item b
		where 	a.nr_sequencia = b.nr_seq_lote
		and 	a.nr_lote_agrupamento = nr_lote_agrupamento_p
		and 	a.ie_status_lote = 'G'
		and 	coalesce(a.ie_agrupamento,'N') = 'N'
		and 	coalesce(b.dt_supensao::text, '') = '';
	
BEGIN

for vetItens in lotesAgrupados loop
	select	coalesce(max('S'),'N')
	into STRICT	ie_material_risco_w
	from	material_risco a
	where (coalesce(a.cd_setor_atendimento::text, '') = '' or a.cd_setor_atendimento = cd_setor_atendimento_p)
	and		a.cd_material = vetItens.cd_material
	and		coalesce(a.ie_material_risco,'N') = 'S';
	
	if (ie_material_risco_w = 'S') then
		goto final;
	end if;
end loop;

<<final>>

return	ie_material_risco_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
