CREATE OR REPLACE FUNCTION obter_se_media_regra ( cd_conta_financ_p bigint) RETURNS varchar AS $body$
DECLARE


ie_media_regra_w	varchar(1);

BEGIN

if (cd_conta_financ_p IS NOT NULL AND cd_conta_financ_p::text <> '') then

	select	coalesce(ie_media_regra,'N')
	into STRICT	ie_media_regra_w
	from	conta_financeira
	where	cd_conta_financ = cd_conta_financ_p;

end if;

return	ie_media_regra_w;

end;
$body$
