SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_medico_assistente (nr_atendimento_p bigint, cd_medico_p text, nm_usuario_p text, qt_hora_p bigint) RETURNS varchar AS $body$
DECLARE


ie_assistente_w	varchar(1) := 'N';


BEGIN
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') and (cd_medico_p IS NOT NULL AND cd_medico_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') and (qt_hora_p IS NOT NULL AND qt_hora_p::text <> '') then

	/* verificar prescricoes */

	select 	coalesce(max('S'),'N')
	into STRICT	ie_assistente_w
	from	prescr_medica
	where	nr_atendimento	= nr_atendimento_p
	and	dt_prescricao		between clock_timestamp() - qt_hora_p/24 and clock_timestamp()
	and	nm_usuario_original	= nm_usuario_p
	and	cd_medico		= cd_medico_p;

	/* verificar evolucoes */

	if (ie_assistente_w = 'N') then
	select	coalesce(max('S'),'N')
	into STRICT	ie_assistente_w
	from	evolucao_paciente
	where	nr_atendimento	= nr_atendimento_p
	and	dt_evolucao		between clock_timestamp() - qt_hora_p/24 and clock_timestamp()
	and	((cd_medico		= cd_medico_p
	and	nm_usuario		= nm_usuario_p)
	or (cd_medico_parecer	= cd_medico_p));
	end if;
end if;

return ie_assistente_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
