CREATE OR REPLACE FUNCTION obter_se_medico_atend_pa (cd_medico_p bigint, qt_horas_p bigint) RETURNS varchar AS $body$
DECLARE


ie_atend_w	varchar(1);


BEGIN

if (cd_medico_p IS NOT NULL AND cd_medico_p::text <> '') and (qt_horas_p IS NOT NULL AND qt_horas_p::text <> '') then
	select 	coalesce(max('S'),'N')
	into STRICT	ie_atend_w
	from 	PEP_ATENDIMENTO_PS_V
	where 	cd_medico_resp = cd_medico_p
	and   	dt_entrada between (clock_timestamp() - (qt_horas_p / 24)) and clock_timestamp()
	and   	coalesce(hr_fim_consulta::text, '') = ''
	and   	coalesce(dt_alta::text, '') = ''
	and	ie_tipo_atendimento = 3;
end if;

return	ie_atend_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
