CREATE OR REPLACE FUNCTION obter_se_medico_cred_atend ( cd_medico_p text, cd_pessoa_fisica_p text, nr_atendimento_p bigint, ie_corpo_clinico_p text) RETURNS varchar AS $body$
DECLARE

			 
ie_pf_credenciada_w		varchar(2);
ie_retorno_w			varchar(1);
qt_credenciamento_w       integer;

BEGIN
if (ie_corpo_clinico_p	= 'S') then 
	ie_retorno_w	:= 'S';
else 
	begin 
	 
	ie_pf_credenciada_w := consistir_credenciamento_pf(coalesce(wheb_usuario_pck.get_cd_estabelecimento,0), coalesce(obter_especialidade_medico(cd_medico_p,'C'),0), cd_pessoa_fisica_p, clock_timestamp(), cd_medico_p, nr_atendimento_p, null, ie_pf_credenciada_w);
	ie_retorno_w	:= 'S';
	 
	if (ie_pf_credenciada_w	= 'N') then 
		ie_retorno_w	:= 'N';
	end if;
	 
	end;
end if;
return	ie_retorno_w;
 
end;
$body$
