CREATE OR REPLACE FUNCTION obter_se_medico_disp_agenda ( dt_agenda_p timestamp, nr_minuto_duracao_p bigint, cd_medico_p text) RETURNS varchar AS $body$
DECLARE



ie_existe_w  varchar(1);
ie_indisponivel_w  varchar(1);
dt_agendamento_w	timestamp;
dt_inicio_indisp_w	timestamp;
dt_fim_indisp_w		timestamp;
ds_retorno_w		varchar(1) := 'S';


C01 CURSOR FOR
	SELECT	dt_inicio,
		dt_fim
	from	indisp_medico_cirurgia
	where	cd_medico  = cd_medico_p
	and	coalesce(ie_situacao,'A') = 'A';


BEGIN
if (cd_medico_p IS NOT NULL AND cd_medico_p::text <> '') then
	select	coalesce(max('S'),'N')
	into STRICT 	ie_existe_w
	from	indisp_medico_cirurgia
	where	cd_medico  = cd_medico_p
	and	coalesce(ie_situacao,'A') = 'A';

	if (ie_existe_w = 'S') then
		open c01;
		loop
		fetch c01 into
			dt_inicio_indisp_w,
			dt_fim_indisp_w;
			EXIT WHEN NOT FOUND; /* apply on c01 */
			begin

			select	coalesce(max('S'),'N')
			into STRICT	ie_indisponivel_w
			
			where	((dt_agenda_p between dt_inicio_indisp_w and dt_fim_indisp_w) or
				 (dt_agenda_p + ((nr_minuto_duracao_p) / 1440) - (1/86400) between dt_inicio_indisp_w and dt_fim_indisp_w) or
				((dt_agenda_p < dt_inicio_indisp_w) and (dt_agenda_p + ((nr_minuto_duracao_p) / 1440) - (1/86400) > dt_fim_indisp_w)));

			if (ie_indisponivel_w = 'S') then
				ds_retorno_w := 'N';
			end if;

			end;
		end loop;
		close c01;
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
