CREATE OR REPLACE FUNCTION obter_se_medico_equipe ( nr_seq_equipe_P bigint, cd_pessoa_fisica_p bigint) RETURNS varchar AS $body$
DECLARE

ie_existe_w		varchar(1) := 'N';


BEGIN

if (nr_seq_equipe_p IS NOT NULL AND nr_seq_equipe_p::text <> '') and (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
	select 	coalesce(max('S'),'N')
	into STRICT 	ie_existe_w
	from 	pf_equipe_partic
	where 	nr_seq_equipe 	 = nr_seq_equipe_P
	and 	cd_pessoa_fisica = cd_pessoa_fisica_p;

	if (ie_existe_w	= 'N') then
			select 	coalesce(max('S'),'N')
		into STRICT 	ie_existe_w
		from 	pf_equipe
		where 	nr_sequencia 	 = nr_seq_equipe_P
		and 	cd_pessoa_fisica = cd_pessoa_fisica_p;
	end if;
end if;

return	ie_existe_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
