CREATE OR REPLACE FUNCTION obter_se_medico_especialidade (NR_SEQ_REGRA_P bigint, CD_MEDICO_ATEND_P text) RETURNS varchar AS $body$
DECLARE


DS_RETORNO_W			varchar(1) := 'S';
CD_ESPEC_MEDICO_ATEND_W		integer;
NR_COUNT_W			bigint := 0;


BEGIN

Select	coalesce(max(cd_espec_medic_atend),0)
into STRICT	cd_espec_medico_atend_w
from	regra_lanc_automatico
where	nr_sequencia = NR_SEQ_REGRA_P;

if (cd_espec_medico_atend_w > 0) then

	Select	count(*)
	into STRICT	nr_count_w
	from	medico_especialidade
	where	cd_pessoa_fisica = CD_MEDICO_ATEND_P
	and	cd_especialidade = CD_ESPEC_MEDICO_ATEND_W;

	if (nr_count_w = 0) then
		ds_retorno_w	:= 'N';
	end if;
end if;

RETURN DS_RETORNO_W;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
