CREATE OR REPLACE FUNCTION obter_se_medico_estab (cd_medico_p text, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(1):= 'S';
cd_empresa_w	smallint;


BEGIN

select	coalesce(max(cd_empresa),0)
into STRICT	cd_empresa_w
from	estabelecimento
where	cd_estabelecimento = cd_estabelecimento_p;

if (cd_medico_p IS NOT NULL AND cd_medico_p::text <> '') and (cd_estabelecimento_p IS NOT NULL AND cd_estabelecimento_p::text <> '') then

	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ds_retorno_w
	from	medico_estabelecimento
	where	cd_pessoa_fisica				= cd_medico_p
	and	coalesce(cd_estabelecimento, cd_estabelecimento_p)	= cd_estabelecimento_p
	and	coalesce(cd_empresa, cd_empresa_w)			= cd_empresa_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
