CREATE OR REPLACE FUNCTION obter_se_medico_exec_laudo ( cd_convenio_p bigint, cd_procedimento_p bigint, ie_origem_proced_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ie_medico_exec_laudo_w	varchar(1)	:= 'N';
qt_regras_w		bigint	:= 0;

cd_grupo_proc_w		grupo_proc.cd_grupo_proc%type;
cd_especialidade_w	especialidade_proc.cd_especialidade%type;
cd_area_procedimento_w	area_procedimento.cd_area_procedimento%type;

nr_seq_regra_w		regra_medico_exec_laudo.nr_sequencia%type;


BEGIN

select	coalesce(max(1),0)
into STRICT	qt_regras_w
from	regra_medico_exec_laudo LIMIT 1;

if (coalesce(qt_regras_w,0) > 0) then

	select	max(cd_grupo_proc),
		max(cd_especialidade),
		max(cd_area_procedimento)
	into STRICT	cd_grupo_proc_w,
		cd_especialidade_w,
		cd_area_procedimento_w
	from	estrutura_procedimento_v
	where	cd_procedimento = cd_procedimento_p
	and	ie_origem_proced = ie_origem_proced_p;

	select	coalesce(max('S'),'N')
	into STRICT	ie_medico_exec_laudo_w
	from	regra_medico_exec_laudo
	where	coalesce(cd_convenio, coalesce(cd_convenio_p,0)) = coalesce(cd_convenio_p,0)
	and	coalesce(cd_procedimento, coalesce(cd_procedimento_p,0)) = coalesce(cd_procedimento_p,0)
	and	coalesce(ie_origem_proced, coalesce(ie_origem_proced_p,0)) = coalesce(ie_origem_proced_p,0)
	and	coalesce(cd_grupo_proc, coalesce(cd_grupo_proc_w,0)) = coalesce(cd_grupo_proc_w,0)
	and	coalesce(cd_especialidade, coalesce(cd_especialidade_w,0)) = coalesce(cd_especialidade_w,0)
	and	coalesce(cd_area_procedimento, coalesce(cd_area_procedimento_w,0)) = coalesce(cd_area_procedimento_w,0)  LIMIT 1;

end if;

return	ie_medico_exec_laudo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
