CREATE OR REPLACE FUNCTION obter_se_medico_familia ( cd_pessoa_fisica_p pessoa_fisica.cd_pessoa_fisica%type, nr_seq_familia_p psf_pessoa_fisica.nr_seq_familia%type) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(1) 	:= 'S';
cd_medico_w			pessoa_fisica.cd_medico%type;
qt_medico_dif_w			bigint	:= 0;
nr_seq_familia_w		psf_pessoa_fisica.nr_seq_familia%type;


BEGIN

begin
select	cd_medico
into STRICT	cd_medico_w
from	pessoa_fisica
where	cd_pessoa_fisica = cd_pessoa_fisica_p  LIMIT 1;
exception
when others then
	cd_medico_w := 'X';
end;

nr_seq_familia_w := nr_seq_familia_p;
if (coalesce(nr_seq_familia_w, 0) = 0) then
	select	max(a.nr_seq_familia)
	into STRICT	nr_seq_familia_w
	from	psf_pessoa_fisica a
	where	a.cd_pessoa_fisica = cd_pessoa_fisica_p;
end if;

if (coalesce(cd_medico_w,'X') <> 'X') then
	begin

	select	count(1)
	into STRICT	qt_medico_dif_w
	from	psf_pessoa_fisica a,
		pessoa_fisica b
	where (a.nr_seq_familia = nr_seq_familia_w)
	and	a.cd_pessoa_fisica <> cd_pessoa_fisica_p
	and	a.cd_pessoa_fisica = b.cd_pessoa_fisica
	and	b.cd_medico <> cd_medico_w  LIMIT 1;

	if (qt_medico_dif_w > 0) then
		begin
		ds_retorno_w := 'N';
		end;
	end if;
	end;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
