CREATE OR REPLACE FUNCTION obter_se_medico_laudante ( nr_Seq_laudo_p bigint, nm_usuario_p text, ie_opcao_p bigint) RETURNS varchar AS $body$
DECLARE


/*
ie_opcao:

0 - Usuário esta vinculado a pasta médico

*/
ds_retorno_w		varchar(1);
qt_medico_w		integer;
cd_pessoa_fisica_W	varchar(10);


BEGIN

ds_retorno_w	:= 'N';

select max(cd_pessoa_fisica)
into STRICT 	cd_pessoa_fisica_W
from 	usuario
where 	nm_usuario = nm_usuario_p;

if (cd_pessoa_fisica_W IS NOT NULL AND cd_pessoa_fisica_W::text <> '') then

	select 	count(*)
	into STRICT	qt_medico_w
	from 	laudo_paciente
	where	cd_medico_resp = cd_pessoa_fisica_W
	and	nr_sequencia	 = nr_Seq_laudo_p;

	if (qt_medico_w > 0) then
		ds_retorno_w := 'S';
	end if;

	if (ds_retorno_w = 'N') then

		select 	count(*)
		into STRICT	qt_medico_w
		from	laudo_paciente_medico
		where	nr_seq_laudo	 = nr_Seq_laudo_p
		and	cd_medico = cd_pessoa_fisica_W;

		if (qt_medico_w > 0) then
			ds_retorno_w := 'S';
		end if;
	end if;

end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
