CREATE OR REPLACE FUNCTION obter_se_medico_lib ( cd_estabelecimento_p bigint, cd_medico_p text, cd_convenio_p bigint) RETURNS varchar AS $body$
DECLARE


qt_registro_w		bigint;
ds_retorno_w		varchar(10);


BEGIN

select	count(*)
into STRICT	qt_registro_w
from	conv_medico_liberacao
where	cd_medico		= cd_medico_p
and	ie_situacao		= 'A'
and	cd_estabelecimento	= cd_estabelecimento_p;

ds_retorno_w	:= 'S';

if (qt_registro_w	>0) then
	ds_retorno_w	:= 'N';

	select	count(*)
	into STRICT	qt_registro_w
	from	conv_medico_liberacao
	where	cd_medico		= cd_medico_p
	and	ie_situacao		= 'A'
	and	cd_convenio		= cd_convenio_p
	and	cd_estabelecimento	= cd_estabelecimento_p;
	if (qt_registro_w	> 0) then
		ds_retorno_w	:= 'S';
	end if;
end if;


return	ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
