CREATE OR REPLACE FUNCTION obter_se_medico_orc_padrao ( cd_medico_p text, nr_seq_orc_padrao_p bigint) RETURNS varchar AS $body$
DECLARE


qt_reg_w		bigint;
ds_retorno_w		varchar(1) := 'S';


BEGIN

select 	count(*)
into STRICT	qt_reg_w
from	orc_padrao_lib_medico a
where	a.nr_seq_orc_padrao = nr_seq_orc_padrao_p;

if (qt_reg_w > 0) and (cd_medico_p IS NOT NULL AND cd_medico_p::text <> '') then

	select 	CASE WHEN count(*)=0 THEN  'N'  ELSE 'S' END
	into STRICT	ds_retorno_w
	from	orc_padrao_lib_medico a
	where	a.nr_seq_orc_padrao = nr_seq_orc_padrao_p
	and	cd_medico = cd_medico_p;

end if;

return	ds_retorno_w;

end;
$body$
