CREATE OR REPLACE FUNCTION obter_se_medico_partic_cir (cd_medico_p text, nr_cirurgia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w  varchar(1);
qt_partic_medico_w bigint;


BEGIN

qt_partic_medico_w  := 0;

IF (cd_medico_p IS NOT NULL AND cd_medico_p::text <> '') AND (nr_cirurgia_p IS NOT NULL AND nr_cirurgia_p::text <> '') THEN

 SELECT  COUNT(*)
 INTO STRICT qt_partic_medico_w
 FROM  cirurgia_participante
 WHERE   cd_pessoa_fisica = cd_medico_p
 AND nr_cirurgia      = nr_cirurgia_p;

 IF (qt_partic_medico_w > 0) THEN
  ds_retorno_w := 'S';
 ELSE
  ds_retorno_w := 'N';
 END IF;
END IF;


RETURN ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
