CREATE OR REPLACE FUNCTION obter_se_medico_plantao (cd_medico_p text, dt_parametro_p timestamp) RETURNS bigint AS $body$
DECLARE



/*
1 - Médico está plantao
0 - Médico não está plantao
*/
qt_plantao_w	integer;


BEGIN

select 	coalesce(count(*),0)
into STRICT   	qt_plantao_w
from	escala_diaria
where	cd_pessoa_fisica = cd_medico_p
and	dt_parametro_p between dt_inicio and dt_fim;

if (qt_plantao_w > 0) then
	return 1;
else
	return 0;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
