CREATE OR REPLACE FUNCTION obter_se_medico_plantonista ( cd_estabelecimento_p bigint, cd_pessoa_fisica_p text, cd_convenio_p bigint, cd_prestador_p text, cd_especialidade_p bigint) RETURNS varchar AS $body$
DECLARE



ie_medico_ie_plantonista_w		varchar(1);

C01 CURSOR FOR
	SELECT	coalesce(ie_plantonista,'N')
	from	medico_convenio
	where	cd_pessoa_fisica		= cd_pessoa_fisica_p
	and	cd_convenio			= cd_convenio_p
	and	((cd_estabelecimento = cd_estabelecimento_p) or (coalesce(cd_estabelecimento::text, '') = ''))
	and 	((coalesce(cd_prestador,'0')	= coalesce(cd_prestador_p, '0')) or (coalesce(cd_prestador::text, '') = ''))
	and 	((coalesce(cd_especialidade,0) = coalesce(cd_especialidade_p,0)) or (coalesce(cd_especialidade::text, '') = ''))
	order by
		coalesce(cd_estabelecimento,0),
		coalesce(cd_especialidade,0),
		somente_numero(coalesce(cd_prestador,'0'));


BEGIN

ie_medico_ie_plantonista_w			:= 'N';

OPEN C01;
LOOP
FETCH C01 into
	ie_medico_ie_plantonista_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	ie_medico_ie_plantonista_w		:= ie_medico_ie_plantonista_w;
END LOOP;
CLOSE C01;

RETURN ie_medico_ie_plantonista_w;

END;
