CREATE OR REPLACE FUNCTION obter_se_medico_prescr (nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE

				 
ds_retorno_w		varchar(100);
nr_prescricao_w		bigint;
nm_medico_presc_w	varchar(100);


BEGIN 
 
select	nr_prescricao, 
		substr(obter_nome_medico(cd_medico_prescritor, 'N'),1,100) 
into STRICT	nr_prescricao_w, 
		nm_medico_presc_w 
from	material_atend_paciente 
where	nr_sequencia = nr_sequencia_p;
 
if (coalesce(nr_prescricao_w, 0) = 0) then  
	ds_retorno_w := nm_medico_presc_w;
end if;
 
if 	((coalesce(nr_prescricao_w, 0) <> 0) or (coalesce(ds_retorno_w::text, '') = '')) then 
	select	max(substr(obter_nome_medico(cd_medico, 'N'),1,100)) 
	into STRICT	ds_retorno_w 
	from	prescr_medica 
	where	nr_prescricao = nr_prescricao_w;
end if;
 
if (coalesce(ds_retorno_w::text, '') = '') then 
	-- NÃ£o informado 
	ds_retorno_w := wheb_mensagem_pck.get_texto(280937);
end if;
 
return wheb_mensagem_pck.get_texto(297287,'DS_MEDICO='||ds_retorno_w);
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
