CREATE OR REPLACE FUNCTION obter_se_medico_socio (cd_estabelecimento_p bigint, cd_medico_p text) RETURNS varchar AS $body$
DECLARE


ie_socio_w		varchar(100);
cd_empresa_w		smallint;


BEGIN

select	coalesce(max(cd_empresa),0)
into STRICT	cd_empresa_w
from	estabelecimento
where	cd_estabelecimento = cd_estabelecimento_p;

