CREATE OR REPLACE FUNCTION obter_se_medico ( cd_medico_p text, ie_informacao_p text) RETURNS varchar AS $body$
DECLARE


ie_retorno_w			varchar(01);
ie_corpo_clinico_w		varchar(01);
ie_corpo_assist_w		varchar(01);
ie_vinculo_medico_w		smallint;
ie_medico_w			smallint;

/*	Informação
	C = Corpo Clinico
	A = Corpo assistencial
	F = Funcionario
	M = Se é Médico
*/
BEGIN

select	coalesce(max(ie_corpo_clinico),'N'),
	coalesce(max(ie_corpo_assist),'N'),
	coalesce(max(ie_vinculo_medico),0),
	coalesce(max(1),0)
into STRICT
	ie_corpo_clinico_w,
	ie_corpo_assist_w,
	ie_vinculo_medico_w,
	ie_medico_w
from	medico
where	cd_pessoa_fisica	= cd_medico_p
and	coalesce(ie_situacao, 'A')	= 'A';

ie_retorno_w			:= 'N';
if (ie_informacao_p = 'C') then
	ie_retorno_w		:= ie_corpo_clinico_w;
elsif (ie_informacao_p = 'A') then
	ie_retorno_w		:= ie_corpo_assist_w;
elsif (ie_informacao_p = 'F') and (ie_vinculo_medico_w = 1) then
	ie_retorno_w	:= 'S';
elsif (ie_informacao_p = 'M') and (ie_medico_w = 1) then
	ie_retorno_w	:= 'S';
end if;

return ie_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
