CREATE OR REPLACE FUNCTION obter_se_medic_antimicrobiano ( nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ie_antimicrobiano_w	varchar(1) := 'N';


BEGIN
if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then
	begin

	select	coalesce(ie_antimicrobiano,'N')
	into STRICT	ie_antimicrobiano_w
	from	medic_ficha_tecnica
	where	nr_sequencia = nr_sequencia_p;
	end;
end if;
return	ie_antimicrobiano_w;
end;
$body$
LANGUAGE PLPGSQL
