CREATE OR REPLACE FUNCTION obter_se_medic_autor_conv ( cd_mat_presc_p bigint, cd_mat_cabine_p bigint, nr_seq_ordem_p bigint) RETURNS varchar AS $body$
DECLARE



ie_retorno_w	varchar(1);
qt_registro_w	bigint;
cd_convenio_w	integer;

BEGIN

select 	coalesce(max(cd_convenio),0)
into STRICT	cd_convenio_w
from	atend_categoria_convenio a,
	paciente_atendimento b,
	can_ordem_prod c
where	a.nr_atendimento	=	b.nr_atendimento
and	b.nr_seq_atendimento	=	c.nr_seq_atendimento
and	c.nr_sequencia		=	nr_seq_ordem_p;

ie_retorno_w := 'S';

select 	count(nr_sequencia)
into STRICT	qt_registro_w
from	regra_mat_sem_apresentacao
where 	cd_mat_sem_apresent 	= 	cd_mat_presc_p
and	cd_convenio		=	cd_convenio_w;

if (qt_registro_w > 0) and (cd_mat_presc_p IS NOT NULL AND cd_mat_presc_p::text <> '') and (cd_mat_cabine_p IS NOT NULL AND cd_mat_cabine_p::text <> '') and (cd_convenio_w IS NOT NULL AND cd_convenio_w::text <> '') then

	select 	coalesce(max('S'),'N')
	into STRICT	ie_retorno_w
	from	regra_mat_sem_apresentacao
	where	cd_mat_sem_apresent 	= 	cd_mat_presc_p
	and	cd_material		=	cd_mat_cabine_p
	and	cd_convenio		=	cd_convenio_w;
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
