CREATE OR REPLACE FUNCTION obter_se_medic_cid ( cd_material_p bigint, nr_atendimento_p bigint) RETURNS bigint AS $body$
DECLARE


cd_grupo_material_w		integer;
cd_subgrupo_material_w		integer;
cd_classe_material_w		integer;
nr_seq_retorno_w		bigint;
cd_doenca_cid_w			varchar(50);
nr_sequencia_w			bigint;
cd_cid_atend_w			varchar(50);
cd_cid_atend_sec_w		varchar(50);

c01 CURSOR FOR
SELECT	cd_doenca_cid,
	nr_sequencia
from	regra_consiste_mat_cid a
where	coalesce(a.cd_grupo_material, cd_grupo_material_w)		= cd_grupo_material_w
and	coalesce(a.cd_subgrupo_material, cd_subgrupo_material_w)	= cd_subgrupo_material_w
and	coalesce(a.cd_classe_material, cd_classe_material_w)		= cd_classe_material_w
and	coalesce(a.cd_material, cd_material_p)			= cd_material_p
order by coalesce(a.cd_material,0),
	coalesce(a.cd_grupo_material,0),
	coalesce(a.cd_subgrupo_material,0),
	coalesce(a.cd_classe_material,0);


BEGIN

select	cd_grupo_material,
	cd_subgrupo_material,
	cd_classe_material
into STRICT	cd_grupo_material_w,
	cd_subgrupo_material_w,
	cd_classe_material_w
from	estrutura_material_v a
where	a.cd_material	= cd_material_p;

open C01;
loop
fetch C01 into
	cd_doenca_cid_w,
	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	cd_doenca_cid_w	:= cd_doenca_cid_w;
end loop;
close C01;

select	max(Obter_Cid_Atendimento(nr_atendimento_p,'P')),
	max(Obter_Cid_Atendimento(nr_atendimento_p,'S'))
into STRICT	cd_cid_atend_w,
	cd_cid_atend_sec_w
;

if (cd_doenca_cid_w IS NOT NULL AND cd_doenca_cid_w::text <> '') and (cd_doenca_cid_w <> coalesce(cd_cid_atend_w,'XPTO')) and (cd_doenca_cid_w <> coalesce(cd_cid_atend_sec_w,'XPTO')) then
	nr_seq_retorno_w	:= nr_sequencia_w;
end if;

return	nr_seq_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
