CREATE OR REPLACE FUNCTION obter_se_medic_composto_cpoe ( nr_sequencia_p bigint, cd_material_p bigint, dt_inicio_p timestamp) RETURNS char AS $body$
DECLARE


ie_retorno_w	char(1)	:= 'N';
nr_prescricao_w		bigint;
nr_agrupamento_w	bigint;
nr_seq_material_w	bigint;
nr_atendimento_w	bigint;
cd_pessoa_fisica_w	varchar(10);


BEGIN

select	max(nr_atendimento),
		max(cd_pessoa_fisica)
into STRICT	nr_atendimento_w,
		cd_pessoa_fisica_w
from	cpoe_material
where	nr_sequencia	= nr_sequencia_p;

if (nr_atendimento_w IS NOT NULL AND nr_atendimento_w::text <> '') and (nr_atendimento_w > 0) then

	select	max(b.nr_sequencia),
			max(b.nr_agrupamento),
			max(a.nr_prescricao)
	into STRICT	nr_seq_material_w,
			nr_agrupamento_w,
			nr_prescricao_w
	from	prescr_medica a,
			prescr_material b
	where	a.nr_prescricao	= b.nr_prescricao
	and		a.nr_atendimento = nr_atendimento_w
	and		a.cd_pessoa_fisica	= cd_pessoa_fisica_w
	and		b.nr_seq_mat_cpoe	= nr_sequencia_p
	and 	b.cd_material 	= cd_material_p
	and		b.dt_inicio_medic = dt_inicio_p;
else
	select	max(b.nr_sequencia),
			max(b.nr_agrupamento),
			max(a.nr_prescricao)
	into STRICT	nr_seq_material_w,
			nr_agrupamento_w,
			nr_prescricao_w
	from	prescr_medica a,
			prescr_material b
	where	a.nr_prescricao	= b.nr_prescricao
	and		a.cd_pessoa_fisica	= cd_pessoa_fisica_w
	and		b.nr_seq_mat_cpoe	= nr_sequencia_p
	and 	b.cd_material 	= cd_material_p
	and		b.dt_inicio_medic = dt_inicio_p;
end if;


if (nr_prescricao_w IS NOT NULL AND nr_prescricao_w::text <> '') and (nr_seq_material_w IS NOT NULL AND nr_seq_material_w::text <> '') and (nr_agrupamento_w IS NOT NULL AND nr_agrupamento_w::text <> '') then

	select	coalesce(max('S'),'N')
	into STRICT	ie_retorno_w
	from	prescr_material where		nr_prescricao	= nr_prescricao_w
	and		nr_sequencia	<> nr_seq_material_w
	and		nr_agrupamento	= nr_agrupamento_w
	and		ie_agrupador	= 1
	and		coalesce(cd_kit_material::text, '') = ''
	and		dt_inicio_medic = dt_inicio_p LIMIT 1;

end if;

return ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
