CREATE OR REPLACE FUNCTION obter_se_medic_controlado ( cd_material_p bigint, cd_estabelecimento_p bigint default null) RETURNS varchar AS $body$
DECLARE

				
vl_retorno_w		varchar(1);
qt_material_w		integer;
cd_estabelecimento_w	smallint;
ie_receita_w        varchar(1);


BEGIN

if (cd_estabelecimento_p IS NOT NULL AND cd_estabelecimento_p::text <> '') then
cd_estabelecimento_w := cd_estabelecimento_p;
else
cd_estabelecimento_w := coalesce(wheb_usuario_pck.get_cd_estabelecimento,0);
end if;

begin
select ie_receita
into STRICT ie_receita_w
from material
where cd_material = cd_material_p;
exception
   when others then
   ie_receita_w    := null;
end;

select	count(*)
into STRICT	qt_material_w
from	medic_controlado
where	cd_material = cd_material_p
and	ie_situacao = 'A'
and	coalesce(cd_estabelecimento,cd_estabelecimento_w) = cd_estabelecimento_w
and (ie_receita_w = 'S' or coalesce(ie_receita,'N') = 'N');

vl_retorno_w	:= 'N';
if (qt_material_w > 0) then
	vl_retorno_w	:= 'S';
end if;	

return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
