CREATE OR REPLACE FUNCTION obter_se_medic_dupla_chec ( cd_material_p bigint, cd_estabelecimento_p bigint, nr_prescricao_p bigint default null) RETURNS char AS $body$
DECLARE


ie_dupla_checagem_w		char(1) := 'N';
cd_grupo_material_w 	bigint;
cd_subgrupo_material_w 	bigint;
cd_classe_material_w 	bigint;
cd_setor_atendimento_w 	bigint;
qt_idade_w 	bigint;

c01 CURSOR FOR
	SELECT	coalesce(max(ie_dupla_checagem),'N')
	from	medic_dupla_checagem
	where	coalesce(cd_material,cd_material_p) = cd_material_p
	and	coalesce(cd_estabelecimento,cd_estabelecimento_p) = cd_estabelecimento_p
	and	coalesce(cd_setor_atendimento,cd_setor_atendimento_w ) = cd_setor_atendimento_w
    and ( coalesce(qt_idade_w,0) between coalesce(qt_idade_min,0) and coalesce(qt_idade_max,999) or coalesce(qt_idade_w, 0) = 0 )
	and	coalesce(cd_grupo_material,cd_grupo_material_w) = cd_grupo_material_w
	and	coalesce(cd_subgrupo_material,cd_subgrupo_material_w) = cd_subgrupo_material_w
	and	coalesce(cd_classe_material,cd_classe_material_w) = cd_classe_material_w
	order by coalesce(cd_material,0),
		coalesce(cd_classe_material,0),
		coalesce(cd_subgrupo_material,0),
		coalesce(cd_grupo_material,0),
		coalesce(qt_idade_min, 0),
		coalesce(cd_setor_atendimento,0),
		coalesce(cd_estabelecimento,0);
		

BEGIN

if (cd_material_p IS NOT NULL AND cd_material_p::text <> '') then

    select	coalesce(max(ie_dupla_checagem),'N')
    into STRICT	ie_dupla_checagem_w
    from	material
    where	cd_material = cd_material_p;

    if (ie_dupla_checagem_w = 'N' and
     (cd_estabelecimento_p IS NOT NULL AND cd_estabelecimento_p::text <> '')) then

        select 	coalesce(max(cd_grupo_material),0),
                coalesce(max(cd_subgrupo_material),0),
                coalesce(max(cd_classe_material),0)
        into STRICT	cd_grupo_material_w,
                cd_subgrupo_material_w,
                cd_classe_material_w
        from 	estrutura_material_v
        where 	cd_material = cd_material_p;

		if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') then
			select 	coalesce(max(CASE WHEN coalesce(cd_setor_atendimento::text, '') = '' THEN  obter_setor_atendimento(nr_atendimento)  ELSE cd_setor_atendimento END ), 0),
					coalesce(max(obter_idade_pf(cd_pessoa_fisica, clock_timestamp(), 'A')), 0)
			into STRICT 	cd_setor_atendimento_w,
					qt_idade_w
			from 	prescr_medica
			where 	nr_prescricao = nr_prescricao_p;
		end if;

        open c01;
        loop
        fetch c01 into	ie_dupla_checagem_w;
        EXIT WHEN NOT FOUND; /* apply on c01 */		
        end loop;
        close c01;	
			
	end if;
	


end if;

return	ie_dupla_checagem_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
