CREATE OR REPLACE FUNCTION obter_se_medic_extra ( cd_material_p bigint default 0, nr_seq_receita_amb_p bigint DEFAULT NULL) RETURNS varchar AS $body$
DECLARE


ds_retorno_w				varchar(1) := 'N';
cd_mat_generic_bipado_w		material.cd_material_generico%type;
cd_mat_generic_receita_w	material.cd_material_generico%type;
cd_material_w				fa_receita_farmacia_item.cd_material%type;
ie_medic_extra_w			fa_medic_farmacia_amb.ie_medic_extra%type;
	
c01 CURSOR FOR
	SELECT	coalesce(max(cd_material),0)
	from	fa_receita_farmacia_item
	where	nr_seq_receita = nr_seq_receita_amb_p;
	

BEGIN
	
select	coalesce(max(cd_material_generico),0)
into STRICT	cd_mat_generic_bipado_w
from	material
where	cd_material = cd_material_p;
	
select	coalesce(max(ie_medic_extra),'N')
into STRICT	ie_medic_extra_w
from	fa_medic_farmacia_amb
where	cd_material = cd_material_p
and		ie_situacao = 'A';

open c01;
loop
fetch c01 into	
	cd_material_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	if (cd_material_p = cd_material_w) and (cd_material_w > 0) then
		ds_retorno_w := 'S';
	end if;

	if (ds_retorno_w = 'N') then
		if (ie_medic_extra_w = 'S') then
			select	coalesce(max(cd_material_generico),0)
			into STRICT	cd_mat_generic_receita_w
			from	material
			where	cd_material = cd_material_w;
		
			if (cd_mat_generic_bipado_w = cd_mat_generic_receita_w) and (cd_mat_generic_receita_w > 0) then
				ds_retorno_w := 'S';
			end if;
		else
			ds_retorno_w := 'S';
		end if;
	end if;
	end;
end loop;
close c01;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
