CREATE OR REPLACE FUNCTION obter_se_medic_interacao ( cd_material_p bigint, cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE


cd_material_cadastro_w      bigint;
cd_material_atual_w         bigint;
ds_retorno_w                varchar(5) := 'N';

C01 CURSOR FOR
      SELECT c.cd_material_interacao
      from   medic_uso_continuo a,
             material b,
             material_interacao_medic c
      where  a.cd_material = b.cd_material
      and	 b.cd_material = c.cd_material
      and	 (c.cd_material_interacao IS NOT NULL AND c.cd_material_interacao::text <> '')
      and	 a.cd_pessoa_fisica = cd_pessoa_fisica_p;

C02 CURSOR FOR
      SELECT c.cd_material_interacao
      from   material b,
             material_interacao_medic c
      where  b.cd_material = c.cd_material
      and    b.cd_material = cd_material_p;


BEGIN

open C01;
loop
fetch C01 into
	cd_material_cadastro_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

   open C02;
   loop
   fetch C02 into
   	cd_material_atual_w;
   EXIT WHEN NOT FOUND; /* apply on C02 */
   	begin

      if (cd_material_cadastro_w = cd_material_atual_w) then
         ds_retorno_w := 'S';
      end if;

   	end;
   end loop;
   close C02;


	end;
end loop;
close C01;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
