CREATE OR REPLACE FUNCTION obter_se_medic_lib_pac2 ( cd_pessoa_fisica_p text, cd_material_p bigint, dt_prescricao_p timestamp, cd_estabelecimento_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


qt_dias_tratamento_w	double precision := 0;
qt_prescricao_w		double precision;
ie_usa_liberacao_w		varchar(50);
dt_inicio_validade_w	timestamp;

c01 CURSOR FOR
SELECT	coalesce(qt_dias_tratamento,0),
	coalesce(coalesce(dt_inicio_validade,dt_atualizacao),dt_prescricao_p)
from	lib_material_paciente
where	cd_material	= cd_material_p
and	cd_pessoa_fisica = cd_pessoa_fisica_p
and	((ie_usa_liberacao_w = 'N') or (dt_liberacao IS NOT NULL AND dt_liberacao::text <> ''))
and	coalesce(dt_suspenso::text, '') = ''
order by coalesce(coalesce(dt_inicio_validade,dt_atualizacao),dt_prescricao_p) desc;


BEGIN

ie_usa_liberacao_w := obter_param_usuario(47, 1, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ie_usa_liberacao_w);

open C01;
loop
fetch C01 into
	qt_dias_tratamento_w,
	dt_inicio_validade_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	qt_dias_tratamento_w	:= qt_dias_tratamento_w;
	dt_inicio_validade_w	:= dt_inicio_validade_w;
	exit;
	end;
end loop;
close C01;

qt_prescricao_w	:= obter_dias_entre_datas(dt_inicio_validade_w, dt_prescricao_p);

if (qt_prescricao_w >= qt_dias_tratamento_w) then
	return 'N';
else
	return 'S';
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
