CREATE OR REPLACE FUNCTION obter_se_medic_lib_pac ( cd_pessoa_fisica_p text, cd_material_p bigint, nr_prescricao_p bigint) RETURNS varchar AS $body$
DECLARE


qt_lib_w				bigint;
qt_dias_tratamento_w	double precision;
qt_prescricao_w			double precision;
nm_usuario_w			varchar(50);
ie_usa_liberacao_w		varchar(50);
cd_estabelecimento_w	bigint;
dt_inicio_validade_w	timestamp;


BEGIN

select	max(nm_usuario),
		max(cd_estabelecimento)
into STRICT	nm_usuario_w,
		cd_estabelecimento_w
from	prescr_medica
where	nr_prescricao	= nr_prescricao_p;

ie_usa_liberacao_w := obter_param_usuario(47, 1, obter_perfil_ativo, nm_usuario_w, cd_estabelecimento_w, ie_usa_liberacao_w);

select	count(1)
into STRICT	qt_lib_w
from	lib_material_paciente
where	((ie_usa_liberacao_w = 'N') or (dt_liberacao IS NOT NULL AND dt_liberacao::text <> ''))
and		cd_material = cd_material_p
and		cd_pessoa_fisica	= cd_pessoa_fisica_p
and		coalesce(dt_suspenso::text, '') = '';

if (qt_lib_w > 0) then

	if (coalesce(nr_prescricao_p,0) > 0) then
		Select	max(qt_dias_tratamento),
				max(coalesce(dt_inicio_validade,dt_atualizacao))
		into STRICT	qt_dias_tratamento_w,
				dt_inicio_validade_w
		from	lib_material_paciente
		where	((ie_usa_liberacao_w = 'N') or (dt_liberacao IS NOT NULL AND dt_liberacao::text <> ''))
		and		coalesce(dt_suspenso::text, '') = ''
		and		cd_material	= cd_material_p
		and		cd_pessoa_fisica = cd_pessoa_fisica_p;

		select	coalesce(max(obter_dias_entre_datas(dt_inicio_validade_w, a.dt_prescricao)), -1)
		into STRICT	qt_prescricao_w
		from	prescr_material b,
				prescr_medica a
		where	b.cd_material = cd_material_p
		and		a.nr_prescricao = b.nr_prescricao
		and		trunc(a.dt_prescricao)	>= trunc(dt_inicio_validade_w)
		and		a.nr_prescricao		= nr_prescricao_p;

		if (qt_prescricao_w = -1) then
			select	coalesce(max(obter_dias_entre_datas(dt_inicio_validade_w, a.dt_prescricao)), -1)
			into STRICT	qt_prescricao_w
			from	prescr_medica a
			where	trunc(a.dt_prescricao)	>= trunc(dt_inicio_validade_w)
			and		a.nr_prescricao		= nr_prescricao_p;
		end if;

		if (qt_prescricao_w >= qt_dias_tratamento_w) then
			return 'N';
		else
			return 'S';
		end if;
	else
		return 'S';
	end if;
else
	return 'N';
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
