CREATE OR REPLACE FUNCTION obter_se_medic_mistura_quimio (nr_seq_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_existe_w	varchar(1);


BEGIN

select	coalesce(max('S'),'N')
into STRICT	ie_existe_w
from	material b,
	paciente_atend_medic a
where 	a.cd_material = b.cd_material
and	nr_seq_atendimento = nr_seq_atendimento_p
and	b.ie_mistura = 'S';

return	ie_existe_w;

end;
