CREATE OR REPLACE FUNCTION obter_se_medic_nao_conciliado ( cd_pessoa_fisica_p text, cd_material_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w varchar(1);


BEGIN

ds_retorno_w := 'N';

if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '' AND cd_material_p IS NOT NULL AND cd_material_p::text <> '') then

	select	coalesce(max('S'), 'N')
	into STRICT	ds_retorno_w
	from	paciente_medic_uso a
	where	obter_princ_ativo_matmed(a.cd_material) = obter_princ_ativo_matmed(cd_material_p)
	and 	a.cd_pessoa_fisica = cd_pessoa_fisica_p
	and 	(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
	and		not exists (SELECT 1
			from 	prescr_medica b,
					prescr_material c
			where	a.cd_pessoa_fisica = b.cd_pessoa_fisica
			and (b.dt_inicio_prescr between clock_timestamp() - interval '1 days' and clock_timestamp() + interval '1 days')
			and		b.nr_prescricao = c.nr_prescricao
			and		obter_princ_ativo_matmed(c.cd_material) = obter_princ_ativo_matmed(a.cd_material));

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
