CREATE OR REPLACE FUNCTION obter_se_medic_pac_prescr (nr_prescricao_p bigint) RETURNS varchar AS $body$
DECLARE


ie_todos_prescr_w	varchar(1) := 'S';
cd_pessoa_fisica_w	varchar(50);
cont_w			bigint;
cd_material_w		bigint;

c01 CURSOR FOR
SELECT	cd_material
from	paciente_medic_uso a
where	cd_pessoa_fisica	= cd_pessoa_fisica_w
and	exists (select	1
		from	hist_saude_reconciliacao x
		where	nr_seq_pac_hist	= a.nr_sequencia)
and	(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '');


BEGIN

select	max(cd_pessoa_fisica)
into STRICT	cd_pessoa_fisica_w
from	prescr_medica
where	nr_prescricao	= nr_prescricao_p;

open C01;
loop
fetch C01 into
	cd_material_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	select	count(*)
	into STRICT	cont_w
	from	prescr_material
	where	nr_prescricao	= nr_prescricao_p
	and	cd_material	= cd_material_w;

	if (cont_w	= 0) then
		ie_todos_prescr_w	:= 'N';
		exit;
	end if;
end loop;
close C01;


return	ie_todos_prescr_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
