CREATE OR REPLACE FUNCTION obter_se_medic_padronizado ( nr_prescricao_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w 	varchar(1);


BEGIN

select 	coalesce(max('N'),'S')
into STRICT	ds_retorno_w
from	material_estab a,
	prescr_material b
where	b.cd_material = a.cd_material
and	b.nr_prescricao = nr_prescricao_p
and	b.ie_agrupador = 1
and	ie_padronizado = 'N'
and	ie_suspenso <> 'S'
and	cd_estabelecimento = wheb_usuario_pck.get_cd_estabelecimento;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
