CREATE OR REPLACE FUNCTION obter_se_medic_possui_obj_uso (nr_prescricao_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(5):= Wheb_mensagem_pck.get_texto(309848); --'Não';
BEGIN
begin

if (coalesce(nr_prescricao_p,0) > 0) then

	SELECT  coalesce(MAX(Wheb_mensagem_pck.get_texto(309849) /*'Sim'*/
),Wheb_mensagem_pck.get_texto(309848) /*'Não'*/)
	into STRICT	ds_retorno_w
	from 	prescr_material a
	where 	nr_prescricao = nr_prescricao_p
	and 	ie_agrupador = 1
	and	coalesce(dt_suspensao::text, '') = ''
	and	(ie_objetivo IS NOT NULL AND ie_objetivo::text <> '');
end if;

return	ds_retorno_w;

exception
when others then
    null;
end;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
