CREATE OR REPLACE FUNCTION obter_se_medic_princ_comp ( nr_prescricao_p prescr_medica.nr_prescricao%type, nr_seq_material_p prescr_material.nr_sequencia%type, nr_agrupamento_p prescr_material.nr_agrupamento%type) RETURNS varchar AS $body$
DECLARE


ie_item_principal_w	varchar(1) := 'S';
ie_itens_w			varchar(1);

BEGIN

if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_seq_material_p IS NOT NULL AND nr_seq_material_p::text <> '') and (nr_agrupamento_p IS NOT NULL AND nr_agrupamento_p::text <> '') then
		
	select	coalesce(max('S'), 'N')
	into STRICT	ie_itens_w
	from	prescr_material a
	where	a.nr_prescricao		= nr_prescricao_p
	and		a.nr_agrupamento	= nr_agrupamento_p
	and		a.ie_item_superior	= 'S'
	and		a.ie_agrupador		= 1
	and 	coalesce(a.dt_suspensao::text, '') = ''
	and 	coalesce(a.nr_seq_substituto::text, '') = '';

	if (ie_itens_w = 'N') then
		select	coalesce(max('N'), 'S')
		into STRICT	ie_item_principal_w
		from	prescr_material a
		where	a.nr_prescricao		= nr_prescricao_p
		and		a.nr_sequencia		< nr_seq_material_p
		and		a.nr_agrupamento	= nr_agrupamento_p
		and		a.ie_agrupador		= 1
		and 	coalesce(a.nr_seq_substituto::text, '') = '';
	else
		select	coalesce(max('N'), 'S')
		into STRICT	ie_item_principal_w
		from	prescr_material a
		where	a.nr_prescricao		= nr_prescricao_p
		and		a.nr_sequencia		<> nr_seq_material_p
		and		a.ie_item_superior	= 'S'
		and		a.nr_agrupamento	= nr_agrupamento_p
		and		a.ie_agrupador		= 1
	    and 	coalesce(a.dt_suspensao::text, '') = ''
	    and 	coalesce(a.nr_seq_substituto::text, '') = '';
	end if;
end if;

return	ie_item_principal_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
