CREATE OR REPLACE FUNCTION obter_se_medic_princ_sol ( nr_prescricao_p bigint, nr_seq_material_p bigint, nr_seq_solucao_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w	varchar(1);
ie_retorno_ww	varchar(1);

BEGIN

select	coalesce(max(b.ie_ancora_solucao),'N')
into STRICT	ie_retorno_w
from	prescr_material a,
		material b
where	a.cd_material 	= b.cd_material
and		a.nr_prescricao = nr_prescricao_p
and		a.nr_sequencia 	= nr_seq_material_p
and		a.ie_agrupador 	= 4;

if (ie_retorno_w = 'N') and (nr_seq_solucao_p IS NOT NULL AND nr_seq_solucao_p::text <> '') then

	select	coalesce(max(b.ie_ancora_solucao),'N')
	into STRICT	ie_retorno_ww
	from	prescr_material a,
			material b
	where	a.cd_material 			= b.cd_material
	and		a.nr_prescricao 		= nr_prescricao_p
	and		a.nr_sequencia_solucao 	= nr_seq_solucao_p
	and		coalesce(b.ie_ancora_solucao,'N') = 'S'
	and		a.ie_agrupador 	= 4;

	if (ie_retorno_ww = 'N') then
		ie_retorno_w := 'S';
	end if;

end if;

return ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
