CREATE OR REPLACE FUNCTION obter_se_medic_quimio_prescr ( nr_prescricao_p bigint, nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


cd_material_w	integer;
qt_quimio_w	bigint;
ie_quimio_w	varchar(1);


BEGIN

ie_quimio_w	:= 'N';

if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then

	select	cd_material
	into STRICT	cd_material_w
	from	prescr_material
	where	nr_prescricao	= nr_prescricao_p
	and	nr_sequencia	= nr_sequencia_p;

	select	count(*)
	into STRICT	qt_quimio_w
	from	material a
	where	a.ie_mistura	= 'S'
	and	a.cd_material	= cd_material_w;

	if (qt_quimio_w > 0) then
		ie_quimio_w	:= 'S';
	end if;
end if;

return ie_quimio_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
