CREATE OR REPLACE FUNCTION obter_se_medic_quimio (cd_material_p bigint) RETURNS varchar AS $body$
DECLARE


qt_quimio_w	bigint;
ie_quimio_w	varchar(1);


BEGIN

ie_quimio_w	:= 'N';

if (cd_material_p IS NOT NULL AND cd_material_p::text <> '') then
	select	count(*)
	into STRICT	qt_quimio_w
	from	material a
	where	((a.ie_mistura	= 'S') or (a.ie_mistura	= 'R'))
	and	a.cd_material	= cd_material_p;

	if (qt_quimio_w > 0) then
		ie_quimio_w	:= 'S';
	end if;
end if;

return	ie_quimio_w;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
