CREATE OR REPLACE FUNCTION obter_se_medic_sem_grupo ( cd_protocolo_p bigint, cd_tipo_protocolo_p bigint, nr_sequencia_p bigint, cd_estabelecimento_p bigint, cd_setor_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


retorno_w	varchar(1) := 'N';
ds_protocolos_w	varchar(2000) := '';


BEGIN
if (cd_protocolo_p IS NOT NULL AND cd_protocolo_p::text <> '') and (cd_tipo_protocolo_p IS NOT NULL AND cd_tipo_protocolo_p::text <> '') and (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') and (cd_estabelecimento_p IS NOT NULL AND cd_estabelecimento_p::text <> '') and (cd_setor_atendimento_p IS NOT NULL AND cd_setor_atendimento_p::text <> '') then
	begin

	ds_protocolos_w := obter_param_usuario(924, 602, wheb_usuario_pck.get_cd_perfil, wheb_usuario_pck.get_nm_usuario, cd_estabelecimento_p, ds_protocolos_w);

	select  CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	retorno_w
	from    protocolo_medic_material b,
		protocolo_medicacao g,
		protocolo e,
		material m
	where   coalesce(b.nr_seq_grupo::text, '') = ''
	and	m.cd_material  = b.cd_material
	and     e.cd_protocolo = g.cd_protocolo
	and     g.cd_protocolo = b.cd_protocolo
	and     g.nr_sequencia = b.nr_sequencia
	and	obter_se_protocolo_lib_setor(e.cd_protocolo,cd_setor_atendimento_p,null, null) = 'S'
	and     ((e.cd_protocolo = cd_protocolo_p) or (cd_protocolo_p = 0))
	and     ((e.cd_tipo_protocolo = cd_tipo_protocolo_p) or (cd_tipo_protocolo_p = 0))
	and     ((g.nr_sequencia = nr_sequencia_p) or (nr_sequencia_p = 0))
	and     ((e.cd_estabelecimento = cd_estabelecimento_p) or (coalesce(e.cd_estabelecimento::text, '') = ''))
	and     ((e.cd_setor_atendimento = cd_setor_atendimento_p) or (coalesce(e.cd_setor_atendimento::text, '') = ''))
	and     b.ie_medic_rotina = 'S'
	and	m.ie_situacao <> 'I'
	and	coalesce(e.ie_situacao,'A') <> 'I'
	and	coalesce(g.ie_situacao,'A') <> 'I'
	and	((coalesce(ds_protocolos_w::text, '') = '')
		or (obter_se_contido_char(e.cd_tipo_protocolo, replace(ds_protocolos_w,'-',',')) = 'S'))
	and	b.ie_agrupador = 1;
	end;
end if;
return retorno_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
