CREATE OR REPLACE FUNCTION obter_se_med_atend_portaria ( dt_inicial_p timestamp, dt_final_p timestamp, cd_especialidade_p bigint) RETURNS varchar AS $body$
DECLARE



qt_dias_portaria_w	bigint;
qt_dias_atendimento_w	bigint;
ds_retorno_w		varchar(255);


BEGIN
qt_dias_portaria_w	:= 0;
qt_dias_atendimento_w	:= 0;

if (dt_final_p <> to_date('30/12/1899','dd/mm/yyyy')) then
	select 	obter_dias_entre_datas_uteis(dt_inicial_p,dt_final_p)
	into STRICT	qt_dias_atendimento_w
	;

	if (cd_especialidade_p IS NOT NULL AND cd_especialidade_p::text <> '') and (cd_especialidade_p in (3, 4, 9, 20)) then
		qt_dias_portaria_w	:= 7;

		if (qt_dias_atendimento_w > qt_dias_portaria_w) then
			ds_retorno_w := Wheb_mensagem_pck.get_texto(309844); --'Não Atende Portaria';
		else
			ds_retorno_w := Wheb_mensagem_pck.get_texto(309845); --'Atende Portaria';
		end if;


	elsif (cd_especialidade_p not in (3, 4, 9, 20)) then
		qt_dias_portaria_w	:= 14;

		if (qt_dias_atendimento_w > qt_dias_portaria_w) then
			ds_retorno_w := Wheb_mensagem_pck.get_texto(309844); --'Não Atende Portaria';
		else
			ds_retorno_w := Wheb_mensagem_pck.get_texto(309845); --'Atende Portaria';
		end if;

	end if;
else
	ds_retorno_w := Wheb_mensagem_pck.get_texto(309844); --'Não Atende Portaria';
end if;
return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
