CREATE OR REPLACE FUNCTION obter_se_med_cliente ( cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE


qtd_w 		bigint  := 0;
retorno_w		varchar(1) := 'N';


BEGIN

 select 	count(*)
 into STRICT	qtd_w
 from 	med_cliente
 where 	cd_pessoa_fisica 	= cd_pessoa_fisica_p;

 if ( 	qtd_w > 0) then
	retorno_w := 'S';
 end if;

return	retorno_w;

end;
$body$
