CREATE OR REPLACE FUNCTION obter_se_menor_idade ( cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE


/* ie_opcao_p

*/
ds_idade_w			varchar(2);
qt_anos_w			integer	:= 0;







BEGIN

select	obter_idade(p.dt_nascimento, clock_timestamp(), 'A')
into STRICT	qt_anos_w
from	pessoa_fisica p
where	p.cd_pessoa_fisica = cd_pessoa_fisica_p;

if (qt_anos_w >= 18) then
	ds_idade_w := 'N';
elsif (qt_anos_w < 18) then
	ds_idade_w := 'S';
end if;

return ds_idade_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
