CREATE OR REPLACE FUNCTION obter_se_menuitem_personaliz ( nr_seq_objeto_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(2000);

C01 CURSOR FOR
	SELECT	coalesce('S','N')
	from	funcao_popup_regra r
	where 	r.nr_seq_objeto = nr_seq_objeto_p
	and	r.ie_tipo_regra = 'P'
	and   	coalesce(r.cd_estabelecimento,wheb_usuario_pck.get_cd_estabelecimento)		= wheb_usuario_pck.get_cd_estabelecimento
	and 	coalesce(r.cd_perfil, wheb_usuario_pck.get_cd_perfil)	= wheb_usuario_pck.get_cd_perfil
	and 	coalesce(r.cd_funcao, wheb_usuario_pck.get_cd_funcao)	= wheb_usuario_pck.get_cd_funcao
	and 	coalesce(r.nm_usuario_regra, wheb_usuario_pck.get_nm_usuario) 	= wheb_usuario_pck.get_nm_usuario
	ORDER BY coalesce(r.nm_usuario_regra,'AAAAAAAA'),coalesce(r.cd_perfil,0),coalesce(r.cd_estabelecimento,0), coalesce(r.cd_funcao,0);

BEGIN
	ds_retorno_w := null;

	open c01;
	loop
	fetch c01 into
		ds_retorno_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin

		ds_retorno_w := ds_retorno_w;

		end;
	end loop;
	close c01;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
