CREATE OR REPLACE FUNCTION obter_se_mesmo_horario ( ds_horarios_p text, dt_referencia_p timestamp ) RETURNS varchar AS $body$
DECLARE


ds_pri_hor_w	varchar(10);
dt_horario_w	timestamp;
ie_retorno_w	varchar(1) := 'S';


BEGIN

if (ds_horarios_p <> 'SN') and (ds_horarios_p <> 'ACM') and (ds_horarios_p IS NOT NULL AND ds_horarios_p::text <> '')and (dt_referencia_p IS NOT NULL AND dt_referencia_p::text <> '') then
	begin
		ds_pri_hor_w	:= substr(ds_horarios_p || ' ',1,position(' ' in ds_horarios_p || ' '));
		dt_horario_w	:= to_date(to_char(dt_referencia_p,'dd/mm/yyyy') || ' ' || ds_pri_hor_w,'dd/mm/yyyy hh24:mi:ss');

		if (dt_horario_w <> dt_referencia_p) then
			ie_retorno_w	:= 'N';
		end if;
	end;
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
